import React, { useState, useEffect } from 'react';
import { Form, Button, Modal, Table } from 'react-bootstrap';
import Navigation from '../Components/Nav';
import Axios from 'axios';
import { useNavigate } from 'react-router-dom';

function SubCategoryAdd() {
    const navigate = useNavigate();
    const [categories, setCategories] = useState([]);
    const [subCategories, setSubCategories] = useState([]);
    const [categoryId, setCategoryId] = useState('');
    const [subCategory, setSubCategory] = useState('');
    const [errors, setErrors] = useState({});
    const [message, setMessage] = useState('');
    const [showAddModal, setShowAddModal] = useState(false);
    const [showEditModal, setShowEditModal] = useState(false);
    const [editSubCategory, setEditSubCategory] = useState(null);
    const [newSubCategoryName, setNewSubCategoryName] = useState('');

    // Fetch categories from the backend
    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const response = await Axios.get('http://localhost:8000/category');
                setCategories(response.data);
            } catch (error) {
                console.error('Error fetching categories:', error);
            }
        };

        fetchCategories();
    }, []);

    // Fetch Subcategories from the backend
    useEffect(() => {
        const fetchSubCategories = async () => {
            try {
                const response = await Axios.get('http://localhost:8000/subcategory');
                setSubCategories(response.data);
            } catch (error) {
                console.error('Error fetching subcategories:', error);
            }
        };

        fetchSubCategories();
    }, []);

    const handleAddSubmit = async (e) => {
        e.preventDefault();

        // Validate form inputs
        const newErrors = {};
        if (!categoryId) newErrors.CategoryId = 'Category is required';
        if (!subCategory) newErrors.SubCategory = 'Subcategory is required';

        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors);
            return;
        }

        const body = {
            CategoryId: categoryId,
            SubCategory: subCategory,
        };

        try {
            await Axios.post('http://localhost:8000/subcategory', body);
            setCategoryId('');
            setSubCategory('');
            setShowAddModal(false); // Close modal after successful add
            fetchSubCategories(); // Refresh subcategories list
        } catch (error) {
            if (error.response && error.response.data && error.response.data.message) {
                setMessage(error.response.data.message);
            } else {
                setMessage('Error submitting form');
            }
        }
    };

    const handleEditSubmit = async (e) => {
        e.preventDefault();

        if (!editSubCategory || !newSubCategoryName) {
            setMessage('All fields are required');
            return;
        }

        try {
            await Axios.put(`http://localhost:8000/subcategory/${editSubCategory._id}`, {
                SubCategory: newSubCategoryName
            });
            setShowEditModal(false); // Close modal after successful update
            setNewSubCategoryName(''); // Clear input field after update
            fetchSubCategories(); // Refresh subcategories list
        } catch (error) {
            console.error('Error updating subcategory:', error);
            setMessage('Error updating subcategory');
        }
    };

    const handleShowEditModal = (subcategory) => {
        setEditSubCategory(subcategory);
        setNewSubCategoryName(subcategory.SubCategory);
        setShowEditModal(true);
    };

    return (
        <>
            <Navigation />
            <div className="pcenter-form">
                <Button onClick={() => setShowAddModal(true)}>Add Subcategory</Button>
                
                {/* Add Subcategory Modal */}
                <Modal show={showAddModal} onHide={() => setShowAddModal(false)}>
                    <Modal.Header closeButton>
                        <Modal.Title>Add Subcategory</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Form noValidate onSubmit={handleAddSubmit}>
                            <Form.Group controlId="validationCategory" className="mb-3">
                                <Form.Label>Category</Form.Label>
                                <Form.Control
                                    as="select"
                                    value={categoryId}
                                    onChange={(e) => setCategoryId(e.target.value)}
                                    isInvalid={!!errors.CategoryId}
                                >
                                    <option value="">Select Category...</option>
                                    {categories.map((category) => (
                                        <option key={category._id} value={category._id}>
                                            {category.CategoryName}
                                        </option>
                                    ))}
                                </Form.Control>
                                <Form.Control.Feedback type="invalid">
                                    {errors.CategoryId}
                                </Form.Control.Feedback>
                            </Form.Group>

                            <Form.Group controlId="validationSubCategory" className="mb-3">
                                <Form.Label>Sub Category</Form.Label>
                                <Form.Control
                                    type="text"
                                    value={subCategory}
                                    onChange={(e) => setSubCategory(e.target.value)}
                                    isInvalid={!!errors.SubCategory}
                                />
                                <Form.Control.Feedback type="invalid">
                                    {errors.SubCategory}
                                </Form.Control.Feedback>
                            </Form.Group>

                            <Button type="submit">Submit</Button>
                        </Form>
                    </Modal.Body>
                </Modal>

                {/* Edit Subcategory Modal */}
                <Modal show={showEditModal} onHide={() => setShowEditModal(false)}>
                    <Modal.Header closeButton>
                        <Modal.Title>Edit Subcategory</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Form noValidate onSubmit={handleEditSubmit}>
                            <Form.Group controlId="formNewSubCategoryName" className="mb-3">
                                <Form.Label>New Subcategory Name</Form.Label>
                                <Form.Control
                                    type="text"
                                    value={newSubCategoryName}
                                    onChange={(e) => setNewSubCategoryName(e.target.value)}
                                />
                            </Form.Group>
                            <Button type="submit">Update</Button>
                        </Form>
                    </Modal.Body>
                </Modal>
            </div>

            <div>
                <h2>Subcategory List</h2>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>Category Name</th>
                            <th>Subcategory Name</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {subCategories.map((subcategory) => (
                            <tr key={subcategory._id}>
                                <td>{subcategory.CategoryId ? subcategory.CategoryId.CategoryName : 'Loading...'}</td>
                                <td>{subcategory.SubCategory}</td>
                                <td>
                                    <Button onClick={() => handleShowEditModal(subcategory)}>Edit</Button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            </div>
        </>
    );
}

export default SubCategoryAdd;
