import React from 'react';
import { Button, Col, Form, Row } from 'react-bootstrap';
import { Formik } from 'formik';
import * as yup from 'yup';

function ProductAdd() {
    const schema = yup.object().shape({
        ProductName: yup.string().required(),
        Price: yup.string().required(),
        Quantity: yup.string().required(),
        Category: yup.string().required(),
        SubCategory: yup.string().when('Category', {
            is: (val) => !!val,
            then: yup.string().required('Subcategory is required when Category is selected'),
            otherwise: yup.string().notRequired(),
        }),
        File: yup.mixed().required(),
    });

    return (
        <Formik
            validationSchema={schema}
            onSubmit={(values) => {
                console.log(values);
            }}
            initialValues={{
                ProductName: '',
                Price: '',
                Quantity: '',
                Category: '',
                SubCategory: '',
                File: null,
            }}
        >
            {({ handleSubmit, handleChange, values, touched, errors, setFieldValue }) => (
                <Form noValidate onSubmit={handleSubmit}>
                    <Row className="mb-3">
                        <Form.Group as={Col} md="4" controlId="validationProductName">
                            <Form.Label>Product Name</Form.Label>
                            <Form.Control
                                type="text"
                                name="ProductName"
                                value={values.ProductName}
                                onChange={handleChange}
                                isValid={touched.ProductName && !errors.ProductName}
                            />
                            <Form.Control.Feedback type="invalid">
                                {errors.ProductName}
                            </Form.Control.Feedback>
                        </Form.Group>

                        <Form.Group as={Col} md="4" controlId="validationPrice">
                            <Form.Label>Price</Form.Label>
                            <Form.Control
                                type="text"
                                name="Price"
                                value={values.Price}
                                onChange={handleChange}
                                isValid={touched.Price && !errors.Price}
                            />
                            <Form.Control.Feedback type="invalid">
                                {errors.Price}
                            </Form.Control.Feedback>
                        </Form.Group>

                        <Form.Group as={Col} md="4" controlId="validationQuantity">
                            <Form.Label>Quantity</Form.Label>
                            <Form.Control
                                type="text"
                                name="Quantity"
                                value={values.Quantity}
                                onChange={handleChange}
                                isValid={touched.Quantity && !errors.Quantity}
                            />
                            <Form.Control.Feedback type="invalid">
                                {errors.Quantity}
                            </Form.Control.Feedback>
                        </Form.Group>
                    </Row>

                    <Row className="mb-3">
                        <Form.Group as={Col} md="6" controlId="validationCategory">
                            <Form.Label>Category</Form.Label>
                            <Form.Control
                                as="select"
                                name="Category"
                                value={values.Category}
                                onChange={(e) => {
                                    handleChange(e);
                                    setFieldValue('SubCategory', ''); // Reset SubCategory when Category changes
                                }}
                                isValid={touched.Category && !errors.Category}
                            >
                                <option value="">Select Category...</option>
                                <option value="Electronics">Electronics</option>
                                <option value="Clothing">Clothing</option>
                                <option value="Books">Books</option>
                                <option value="Home Decor">Home Decor</option>
                            </Form.Control>
                            <Form.Control.Feedback type="invalid">
                                {errors.Category}
                            </Form.Control.Feedback>
                        </Form.Group>

                        {values.Category && (
                            <Form.Group as={Col} md="6" controlId="validationSubCategory">
                                <Form.Label>Subcategory</Form.Label>
                                <Form.Control
                                    as="select"
                                    name="SubCategory"
                                    value={values.SubCategory}
                                    onChange={handleChange}
                                    isValid={touched.SubCategory && !errors.SubCategory}
                                >
                                    <option value="">Select Subcategory...</option>
                                    {values.Category === 'Electronics' && (
                                        <>
                                            <option value="Mobile Phone">Mobile Phone</option>
                                            <option value="Laptop">Laptop</option>
                                        </>
                                    )}
                                    {values.Category === 'Clothing' && (
                                        <>
                                            <option value="Men's Clothing">Men's Clothing</option>
                                            <option value="Women's Clothing">Women's Clothing</option>
                                        </>
                                    )}
                                    {values.Category === 'Books' && (
                                        <>
                                            <option value="Fiction">Fiction</option>
                                            <option value="Non-Fiction">Non-Fiction</option>
                                        </>
                                    )}
                                    {values.Category === 'Home Decor' && (
                                        <>
                                            <option value="Furniture">Furniture</option>
                                            <option value="Lighting">Lighting</option>
                                        </>
                                    )}
                                </Form.Control>
                                <Form.Control.Feedback type="invalid">
                                    {errors.SubCategory}
                                </Form.Control.Feedback>
                            </Form.Group>
                        )}
                    </Row>

                    <Form.Group controlId="validationFile" className="mb-3">
                        <Form.Label>File</Form.Label>
                        <Form.Control
                            type="file"
                            name="File"
                            onChange={handleChange}
                            isInvalid={!!errors.File}
                        />
                        <Form.Control.Feedback type="invalid">
                            {errors.File}
                        </Form.Control.Feedback>
                    </Form.Group>

                    <Button type="submit">Submit Form</Button>
                </Form>
            )}
        </Formik>
    );
}

export default ProductAdd;
