import React, { useEffect, useState } from 'react';
import Navigation from '../Components/Nav';
import '../Pages/Homepage.css';
import Axios from 'axios';
import { Button } from 'react-bootstrap';
import Table from 'react-bootstrap/Table';

function ProductList() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await Axios.get('http://localhost:8000/products'); // Endpoint to fetch products
                setProducts(response.data); // Set products state with fetched data
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchProducts();
    }, []);


    const handleUpdate = async (productId) => {
      try {
          const response = await Axios.get(`http://localhost:8000/products/${productId}`); // Endpoint to fetch specific product
          // Handle how you want to use the fetched product data for update
          console.log('Product to update:', response.data);
          // Example: Navigate to update page or open a modal to edit the product details
      } catch (error) {
          console.error('Error fetching product for update:', error);
      }
  };

    return (
        <div>
            <Navigation />
            <h2>Product List</h2>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>Product Name</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Category</th>
                        <th>Sub_Category</th>
                        <th>Image</th>
                        <th>Button</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map(product => (
                        <tr key={product._id}>
                            <td>{product.ProductName}</td>
                            <td>{product.Price}</td>
                            <td>{product.Quantity}</td>
                            <td>{product.Category}</td>
                            <td>{product.SubCategory}</td>
                            <td>{product.Image}</td>
                            <td>
                                <Button onClick={() => handleUpdate(product._id)}>Update</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>
        </div>
    );
}

export default ProductList;
