import React, { useState, useEffect } from 'react';
import { Form, Button, Modal, Table } from 'react-bootstrap';
import Navigation from '../Components/Nav';
import Axios from 'axios';
import { useNavigate } from 'react-router-dom';

function SubCategoryAdd() {
    const navigate = useNavigate();
    const [categories, setCategories] = useState([]);
    const [subCategories, setSubCategories] = useState([]);
    const [categoryId, setCategoryId] = useState('');
    const [subCategory, setSubCategory] = useState('');
    const [errors, setErrors] = useState({});
    const [message, setMessage] = useState('');
    const [categoryName, setCategoryName] = useState('');

    // Fetch categories from the backend
    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const response = await Axios.get('http://localhost:8000/category');
                setCategories(response.data);
            } catch (error) {
                console.error('Error fetching categories:', error);
            }
        };

        fetchCategories();
    }, []);

    // Fetch Subcategories from the backend
    useEffect(() => {
        const fetchSubCategories = async () => {
            try {
                const response = await Axios.get('http://localhost:8000/joinsubcategories');
                setSubCategories(response.data);
            } catch (error) {
                console.error('Error fetching subcategories:', error);
            }
        };

        fetchSubCategories();
    }, []);

    const handleFormSubmit = async (e) => {
        e.preventDefault();

        // Validate form inputs
        const newErrors = {};
        if (!categoryId) newErrors.CategoryId = 'Category is required';
        if (!subCategory) newErrors.SubCategory = 'Subcategory is required';

        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors);
            return;
        }

        const body = {
            CategoryId: categoryId,
            SubCategory: subCategory,
        };

        try {
            const response = await Axios.post('http://localhost:8000/subcategory', body);
            console.log('Form submitted successfully:', response.data);
            setCategoryId('');
            setSubCategory('');
            navigate('/home');
        } catch (error) {
            if (error.response && error.response.data && error.response.data.message) {
                setMessage(error.response.data.message);
            } else {
                setMessage('Error submitting form');
            }

        }
    };

    const handleUpdate = async (subcategoryToUpdateId) => {
        try {
            // Fetch subcategory details for updating
            const response = await Axios.get(`http://localhost:8000/subcategory/${subcategoryToUpdateId}`);
            const subcategoryToUpdate = response.data; // Assuming your API returns the subcategory details
            // Implement logic to update the subcategory (e.g., show a modal with form pre-filled)
            console.log('Subcategory to update:', subcategoryToUpdate);
        } catch (error) {
            console.error('Error fetching subcategory details:', error);
        }
    };

    //Once Click the Update Button To Store and DB PUT METHOD
    
    const resetPasswordHandler = async () => {
        console.log(currentCategoryId)
        try {
          await Axios.put(`http://localhost:8000/category/${currentCategoryId}`, {
            CategoryName: newCategoryName
          });
          console.log('Category updated successfully');
          setShowOtpModal(false); // Close modal after successful update
          setSubNewCategoryName(''); // Clear input field after update
          fetchCategories(); // Refresh categories list
        } catch (error) {
          console.error('Error updating category:', error);
          // Handle error updating category
        }
      };



    return (
        <>
            <Navigation />
            <div className="pcenter-form">
                <Form noValidate onSubmit={handleFormSubmit} className="pform">
                    <h4 className="text-center">Add Sub Category</h4>

                    <Form.Group controlId="validationCategory" className="mb-3">
                        <Form.Label>Category</Form.Label>
                        <Form.Control
                            as="select"
                            value={categoryId}
                            onChange={(e) => setCategoryId(e.target.value)}
                            isInvalid={!!errors.CategoryId}
                        >
                            <option value="">Select Category...</option>
                            {categories.map((category) => (
                                <option key={category._id} value={category._id}>
                                    {category.CategoryName}
                                </option>
                            ))}
                        </Form.Control>
                        <Form.Control.Feedback type="invalid">
                            {errors.CategoryId}
                        </Form.Control.Feedback>
                    </Form.Group>

                    <Form.Group controlId="validationSubCategory" className="mb-3">
                        <Form.Label>Sub Category</Form.Label>
                        <Form.Control
                            type="text"
                            value={subCategory}
                            onChange={(e) => setSubCategory(e.target.value)}
                            isInvalid={!!errors.SubCategory}
                        />
                        <Form.Control.Feedback type="invalid">
                            {errors.SubCategory}
                        </Form.Control.Feedback>
                    </Form.Group>

                    <Button className="mx-auto d-block" type="submit">Submit Form</Button>
                    {message && <p>{message}</p>}
                </Form>

                <Modal show={showOtpModal} onHide={() => setShowOtpModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Update Category Name</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formNewCategoryName" className="mb-3">
              <Form.Label>New Category Name</Form.Label>
              <Form.Control
                type="text"
                value={newCategoryName}
                onChange={(e) => setNewCategoryName(e.target.value)}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowOtpModal(false)}>
            Close
          </Button>
          <Button variant="primary" onClick={resetPasswordHandler}>
            Update Name
          </Button>
        </Modal.Footer>
      </Modal>
            </div>

            <div>
                <h2>Subcategory List</h2>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>Category Name</th>
                            <th>Subcategory Name</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {subCategories.map((subcategory) => (
                            <tr key={subcategory._id}>
                                <td>{subcategory.CategoryId ? subcategory.CategoryId.CategoryName : 'Loading...'}</td>
                                <td>{subcategory.SubCategory}</td>
                                <td>
                                    <Button onClick={() => handleUpdate(subcategory._id)}>Update</Button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            </div>
        </>
    );
}

export default SubCategoryAdd;
