import React from 'react'
import Navigation from '../Components/Nav'
import Table from 'react-bootstrap/Table';
import { useEffect, useState } from 'react';
import Axios from 'axios'

function OrderDetails() {
  const [cartItems, setCartItems] = useState([])
  const fetchCartItems = async () => {
    try {
      const response = await Axios.get(`http://localhost:8000/cart/all`);
      setCartItems(response.data);
    } catch (error) {
      console.error('Error fetching cart items:', error);
    }
  };

  useEffect(() => {
    fetchCartItems();
  }, []);

  return (
    <div>
      <div>
        <Navigation />
      </div>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>User Name</th>
            <th>Product Name</th>
            <th>Total Price</th>
            <th>Quantity</th>
            <th>Price</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {products.map(product => (
            <tr key={product._id}>
              <td>{product.ProductName}</td>
              <td>{product.Price}</td>
              <th>{product.Description}</th>
              <td>{product.Quantity}</td>
              <td>{product.Category?.CategoryName}</td>
              <td>{product.SubCategory?.SubCategory}</td>
              <td>
                <img src={product.Image} alt={product.ProductName} style={{ width: '100px', height: 'auto' }} />
              </td>
              <td>
                <Button onClick={() => handleUpdate(product._id)}>Update</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}


export default OrderDetails
