import React from 'react';
import { Button, Form } from 'react-bootstrap';
import { Formik } from 'formik';
import * as yup from 'yup';
import Axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Navigation from '../Components/Nav';
import '../Pages/ProductAdd.css';

// Define validation schema using yup
const schema = yup.object().shape({
  ProductName: yup.string().required('Product Name is required'),
  Price: yup.number().required('Price is required').typeError('Price must be a number'),
  Quantity: yup.number().required('Quantity is required').typeError('Quantity must be a number'),
  Category: yup.string().required('Category is required'),
  SubCategory: yup.string().when('Category', {
    is: (val) => val && val.length > 0,
    then: yup.string().required('Subcategory is required when Category is selected'),
  }),
  File: yup.mixed().required('File is required'),
});

function ProductAdd() {
  const navigate = useNavigate();

  const handleFormSubmit = async (values, { resetForm }) => {
    const formData = new FormData();
    formData.append('name', values.ProductName);
    formData.append('price', values.Price);
    formData.append('quantity', values.Quantity);
    formData.append('category', values.Category);
    formData.append('subcategory', values.SubCategory);
    formData.append('file', values.File);

    try {
      const response = await Axios.post('http://localhost:3001/createproduct/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      console.log('Form submitted successfully:', response.data);
      resetForm(); // Reset the form after successful submission
      navigate('/home');
    } catch (error) {
      if (Axios.isCancel(error)) {
        console.log('Request canceled:', error.message);
      } else if (error.response) {
        console.error('Server responded with error:', error.response.data);
      } else if (error.request) {
        console.error('No response received:', error.request);
      } else {
        console.error('Error setting up the request:', error.message);
      }
      console.error('Error submitting form:', error);
    }
  };

  return (
    <>
      <Navigation />
      <Formik
        validationSchema={schema}
        onSubmit={handleFormSubmit}
        initialValues={{
          ProductName: '',
          Price: '',
          Quantity: '',
          Category: '',
          SubCategory: '',
          File: null,
        }}
      >
        {({ handleSubmit, handleChange, values, touched, errors, setFieldValue }) => (
          <div className="pcenter-form">
            <Form noValidate onSubmit={handleSubmit} className="pform">

              <h4 className="text-center">Product Add Form</h4>

              <Form.Group controlId="validationProductName" className="mb-3">
                <Form.Label>Product Name</Form.Label>
                <Form.Control
                  type="text"
                  name="ProductName"
                  value={values.ProductName}
                  onChange={handleChange}
                  isInvalid={touched.ProductName && !!errors.ProductName}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.ProductName}
                </Form.Control.Feedback>
              </Form.Group>

              <Form.Group controlId="validationPrice" className="mb-3">
                <Form.Label>Price</Form.Label>
                <Form.Control
                  type="text"
                  name="Price"
                  value={values.Price}
                  onChange={handleChange}
                  isInvalid={touched.Price && !!errors.Price}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.Price}
                </Form.Control.Feedback>
              </Form.Group>

              <Form.Group controlId="validationQuantity" className="mb-3">
                <Form.Label>Quantity</Form.Label>
                <Form.Control
                  type="text"
                  name="Quantity"
                  value={values.Quantity}
                  onChange={handleChange}
                  isInvalid={touched.Quantity && !!errors.Quantity}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.Quantity}
                </Form.Control.Feedback>
              </Form.Group>

              <Form.Group controlId="validationCategory" className="mb-3">
                <Form.Label>Category</Form.Label>
                <Form.Control
                  as="select"
                  name="Category"
                  value={values.Category}
                  onChange={(e) => {
                    handleChange(e);
                    setFieldValue('SubCategory', ''); // Reset SubCategory when Category changes
                  }}
                  isInvalid={touched.Category && !!errors.Category}
                >
                  <option value="">Select Category...</option>
                  <option value="Electronics">Electronics</option>
                  <option value="Clothing">Clothing</option>
                  <option value="Home Decor">Home Decor</option>
                </Form.Control>
                <Form.Control.Feedback type="invalid">
                  {errors.Category}
                </Form.Control.Feedback>
              </Form.Group>

              {values.Category && (
                <Form.Group controlId="validationSubCategory" className="mb-3">
                  <Form.Label>Subcategory</Form.Label>
                  <Form.Control
                    as="select"
                    name="SubCategory"
                    value={values.SubCategory}
                    onChange={handleChange}
                    isInvalid={touched.SubCategory && !!errors.SubCategory}
                  >
                    <option value="">Select Subcategory...</option>
                    {values.Category === 'Electronics' && (
                      <>
                        <option value="Mobile Phone">Mobile Phone</option>
                        <option value="Laptop">Laptop</option>
                      </>
                    )}
                    {values.Category === 'Clothing' && (
                      <>
                        <option value="Men's Clothing">Men's Clothing</option>
                        <option value="Women's Clothing">Women's Clothing</option>
                      </>
                    )}
                    {values.Category === 'Home Decor' && (
                      <>
                        <option value="Furniture">Furniture</option>
                        <option value="Lighting">Lighting</option>
                      </>
                    )}
                  </Form.Control>
                  <Form.Control.Feedback type="invalid">
                    {errors.SubCategory}
                  </Form.Control.Feedback>
                </Form.Group>
              )}

              <Form.Group controlId="validationFile" className="mb-3">
                <Form.Label>File</Form.Label>
                <Form.Control
                  type="file"
                  name="File"
                  onChange={(e) => setFieldValue('File', e.target.files[0])}
                  isInvalid={touched.File && !!errors.File}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.File}
                </Form.Control.Feedback>
              </Form.Group>

              <Button className="mx-auto d-block" type="submit">Submit Form</Button>
            </Form>
          </div>
        )}
      </Formik>
    </>
  );
}

export default ProductAdd;
