import React, { useState, useEffect } from 'react';
import { Form, FormGroup, FormLabel, FormControl, Button, Modal } from 'react-bootstrap';
import Navigation from '../Components/Nav';
import '../Pages/ProductAdd.css';
import Axios from 'axios';
import Table from 'react-bootstrap/Table';

function CategoryAdd() {
  const [categoryName, setCategoryName] = useState('');
  const [error, setError] = useState('');

  const handleInputChange = (event) => {
    setCategoryName(event.target.value);
    console.log(categoryName)
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await Axios.post('http://localhost:8000/createCategory', {
        CategoryName: categoryName
      });
      console.log('Form submitted successfully:', response.data);
      setCategoryName(''); // Clear input field after successful submission
      setError(''); // Clear any previous error
    } catch (error) {
      console.error('Error submitting form:', error);
      setError('Error submitting form. Please try again.'); // Generic error message
    }
  };


  //View The Category Name:
  const [category, setCategory] = useState([]);

  useEffect(() => {
      const fetchCategory = async () => {
          try {
              const response = await Axios.get('http://localhost:8000/category'); // Endpoint to fetch category
              setCategory(response.data); // Set Category state with fetched data
          } catch (error) {
              console.error('Error fetching category:', error);
          }
      };

      fetchCategory();
  }, []);

  const handleUpdate = async (categoryid) => {
    console.log(categoryid)
    try {
        // const response = await Axios.get(`http://localhost:8000/category/${categoryid}`);
        // const categoryData = response.data; // Assuming response.data contains product details
        // console.log(categoryData)
    } catch (error) {
        console.error('Error fetching product for update:', error);
    }
};

  return (
    <>
      <Navigation />
      <div className="pcenter-form">
        <Form noValidate onSubmit={handleFormSubmit} className="pform">
          <h4 className="text-center">Category Add Form</h4>
          <Form.Group controlId="validationCategoryName" className="mb-3">
            <Form.Label>Category Name</Form.Label>
            <Form.Control
              type="text"
              name="CategoryName"
              value={categoryName}
              onChange={handleInputChange}
              required
            />
          </Form.Group>
          {error && <div className="text-danger mb-3">{error}</div>}
          <Button className="mx-auto d-block" type="submit">Submit Form</Button>
        </Form>
        <Modal show={showOtpModal} onHide={() => setShowOtpModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Reset Password</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <FormGroup className="mb-3" controlId="formBasicOtp">
                            <FormLabel>OTP</FormLabel>
                            <FormControl 
                                type="text" 
                                placeholder="Enter OTP" 
                                onChange={(e) => setOtp(e.target.value)} 
                                value={otp} 
                            />
                        </FormGroup>
                        <FormGroup className="mb-3" controlId="formBasicPassword">
                            <FormLabel>New Password</FormLabel>
                            <FormControl 
                                type="password" 
                                placeholder="Enter new password" 
                                onChange={(e) => setNewPassword(e.target.value)} 
                                value={newPassword} 
                            />
                        </FormGroup>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowOtpModal(false)}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={resetPasswordHandler}>
                        Reset Password
                    </Button>
                </Modal.Footer>
            </Modal>
      </div>
      <div>
      <div>
            <Navigation />
            <h2
            >Category List</h2>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>Category Name</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {category.map(category => (
                        <tr key={category._id}>
                            <td>{category.CategoryName}</td>
                            <td>
                                <Button onClick={() => handleUpdate(category._id)}>Update</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>
        </div>
      </div>
    </>
  );
}

export default CategoryAdd;





