    //           <Route path="/productadd" element={<ProductAdd />} />
    //         </>
    //       ) : (
    //         <Route path="*" element={<Navigate to="/login" replace />} />
    //       )}
    //     </Routes>
    //   </div>
    // </Router>
    <Router>
    <div className="content content-scrollable">
      <Routes>
        <Route path="/login" element={<LoginPage />} />
        {token ? (
          <>
            <Route path="/home" element={<HomePage />} />
            <Route path="/productadd" element={<ProductAdd />} />
          </>
        ) : (
          <Route path="*" element={<Navigate to="/login" replace />} />
        )}
      </Routes>
    </div>
  </Router>
  );
}

export default App;
