import React, { useState, useEffect } from 'react';
import { Button, Form } from 'react-bootstrap';
import Navigation from '../Components/Nav';
import '../Pages/ProductAdd.css';
import Axios from 'axios';
import { useNavigate } from 'react-router-dom';

function SubCategoryAdd() {
  const navigate = useNavigate();
  const [categories, setCategories] = useState([]);
  const [formValues, setFormValues] = useState({
    CategoryId: '',
    SubCategory: '',
  });
  const [errors, setErrors] = useState({});

  // Fetch categories from the backend
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await Axios.get('http://localhost:8000/categories/');
        setCategories(response.data);
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };

    fetchCategories();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    const newFormValues = { ...formValues };
    newFormValues[name] = value;
    setFormValues(newFormValues);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    // Validate form inputs
    const newErrors = {};
    if (!formValues.CategoryId) newErrors.CategoryId = 'Category is required';
    if (!formValues.SubCategory) newErrors.SubCategory = 'Subcategory is required';

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    const formData = new FormData();
    Object.keys(formValues).forEach((key) => {
      formData.append(key, formValues[key]);
    });

    try {
      const response = await Axios.post('http://localhost:8000/subcategory/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      console.log('Form submitted successfully:', response.data);
      setFormValues({
        CategoryId: '',
        SubCategory: '',
      });
      navigate('/home');
    } catch (error) {
      if (Axios.isCancel(error)) {
        console.log('Request canceled:', error.message);
      } else if (error.response) {
        console.error('Server responded with error:', error.response.data);
      } else if (error.request) {
        console.error('No response received:', error.request);
      } else {
        console.error('Error setting up the request:', error.message);
      }
      console.error('Error submitting form:', error);
    }
  };

  return (
    <>
      <Navigation />
      <div className="pcenter-form">
        <Form noValidate onSubmit={handleFormSubmit} className="pform">
          <h4 className="text-center">Add Sub Category</h4>

          
          <Form.Group controlId="validationSubCategory" className="mb-3">
            <Form.Label>Sub Category</Form.Label>
            <Form.Control
              type="text"
              name="SubCategory"
              value={formValues.SubCategory}
              onChange={handleChange}
              isInvalid={!!errors.SubCategory}
            />
            <Form.Control.Feedback type="invalid">
              {errors.SubCategory}
            </Form.Control.Feedback>
          </Form.Group>

          <Button className="mx-auto d-block" type="submit">Submit Form</Button>
        </Form>
      </div>
    </>
  );
}

export default SubCategoryAdd;
