import React from 'react'
import Navigation from '../Components/Nav'
import Table from 'react-bootstrap/Table';
import { useEffect, useState } from 'react';
import Axios from 'axios'

function OrderDetails() {
  const [cartItems, setCartItems] = useState([])
  const fetchCartItems = async () => {
    try {
      const response = await Axios.get(`http://localhost:8000/cart/alla`);
      setCartItems(response.data);
    } catch (error) {
      console.error('Error fetching cart items:', error);
    }
  };

  useEffect(() => {
    fetchCartItems();
  }, []);
  console.log(cartItems)
  return (
    <div>
      <div>
        <Navigation />
      </div>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>User Name</th>
            <th>Product Name</th>
            <th>Total Price</th>
            <th>Quantity</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map(cart => (
            <tr key={cart._id}>
              <td>{cart.userId.name}</td>
              <td>{cart.productId.ProductName}</td>
              <th>{cart.productId.Price}</th>
              <td>{cart.Quantity}</td>
              <td>{cart.Category?.CategoryName}</td>
              <td>{cart.SubCategory?.SubCategory}</td>
              {/* <td>
                <img src={product.Image} alt={product.ProductName} style={{ width: '100px', height: 'auto' }} />
              </td> */}
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}


export default OrderDetails
