import React, { useState, useEffect } from 'react';
import { Form, Button, Table } from 'react-bootstrap';
import Navigation from '../Components/Nav';
import Axios from 'axios';
import { useNavigate } from 'react-router-dom';

function SubCategoryAdd() {
    const navigate = useNavigate();
    const [categories, setCategories] = useState([]);
    const [subCategories, setSubCategories] = useState([]);
    const [categoryId, setCategoryId] = useState('');
    const [subCategory, setSubCategory] = useState('');
    const [errors, setErrors] = useState({});

    // Fetch categories from the backend Category
    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const response = await Axios.get('http://localhost:8000/category');
                setCategories(response.data);
            } catch (error) {
                console.error('Error fetching categories:', error);
            }
        };

        fetchCategories();
    }, []);

    // Fetch Subcategories from the backend Category
    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const response = await Axios.get('http://localhost:8000/joinsubcategories');
                setSubCategories(response.data);
            } catch (error) {
                console.error('Error fetching categories:', error);
            }
        };

        fetchCategories();
    }, []);

    console.log(subCategories)


    const handleFormSubmit = async (e) => {
        e.preventDefault();

        // Validate form inputs
        const newErrors = {};
        if (!categoryId) newErrors.CategoryId = 'Category is required';
        if (!subCategory) newErrors.SubCategory = 'Subcategory is required';

        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors);
            return;
        }

        const body = {
            CategoryId: categoryId,
            SubCategory: subCategory,
        }

        console.log(body)
        console.log(categoryId)

        //Post The SubCategory
        try {
            const response = await Axios.post('http://localhost:8000/subcategory', body
            );
            console.log('Form submitted successfully:', response.data);
            setCategoryId('');
            setSubCategory('');
            navigate('/home');
        } catch (error) {
            console.error('Error submitting form:', error);
        }
    };

    //get the Value in Sub Categories
    const handleUpdate = async (categoryId) => {
        const categoryToUpdate = categories.find(category => category._id === categoryId);
        if (categoryToUpdate) {
        }
    };

    return (
        <>
            <Navigation />
            <div className="pcenter-form">
                <Form noValidate onSubmit={handleFormSubmit} className="pform">
                    <h4 className="text-center">Add Sub Category</h4>

                    <Form.Group controlId="validationCategory" className="mb-3">
                        <Form.Label>Category</Form.Label>
                        <Form.Control
                            as="select"
                            value={categoryId}
                            onChange={(e) => setCategoryId(e.target.value)}
                            isInvalid={!!errors.CategoryId}
                        >
                            <option value="">Select Category...</option>
                            {categories.map(category => (
                                <option key={category._id} value={category._id}>
                                    {category.CategoryName}
                                </option>
                            ))}
                        </Form.Control>
                        <Form.Control.Feedback type="invalid">
                            {errors.CategoryId}
                        </Form.Control.Feedback>
                    </Form.Group>

                    <Form.Group controlId="validationSubCategory" className="mb-3">
                        <Form.Label>Sub Category</Form.Label>
                        <Form.Control
                            type="text"
                            value={subCategory}
                            onChange={(e) => setSubCategory(e.target.value)}
                            isInvalid={!!errors.SubCategory}
                        />
                        <Form.Control.Feedback type="invalid">
                            {errors.SubCategory}
                        </Form.Control.Feedback>
                    </Form.Group>

                    <Button className="mx-auto d-block" type="submit">Submit Form</Button>
                </Form>
            </div>
            <div>
                <h2>Category List</h2>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>Category Name</th>
                            <th>Subcategory Name</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {subCategories.map((subcategory) => (
                            <tr key={subcategory._id}>
                                <td>{subcategory.CategoryId.CategoryName}</td>
                                <td>{subcategory.SubCategoryName}</td>
                                <td>
                                    <Button onClick={() => handleUpdate(subCategory._id)}>Update</Button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            </div>
        </>
    );
}

export default SubCategoryAdd;
