import React from 'react'
import { useState } from 'react';
import { Form, FormGroup, FormLabel, FormControl, Button} from 'react-bootstrap';
import Axios from 'axios'
import { useNavigate } from "react-router-dom"
import '../Pages/LoginPage.css';


function LoginPage({ setIsAuthenticated }) {
    const Navigate = useNavigate();
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const LoginHandler = async (e) => {
        e.preventDefault();
        try {
            const response = await Axios.post('http://localhost:3001/login', { email, password });
            const token = response.data.token
            localStorage.setItem('token', token)
            alert("Login Succesfully")
            Navigate('/home');
        } catch (error) {
            console.error('Login failed:', error.response ? error.response.data.message : error.message);
            alert("Login failed. Please check your credentials."); 
        }
    };

    return (
        <>
            <dic className="center-container">
                <Form className="form" onSubmit={LoginHandler}>
                    <FormGroup className="mb-3" controlId="formBasicEmail">
                        <h4>Admin Page</h4>
                        <FormLabel>User Name</FormLabel>
                        <FormControl type="email" placeholder="Enter username" onChange={(e) => setEmail(e.target.value)} value={email} />
                    </FormGroup>
                    <FormGroup className="mb-3" controlId="formBasicPassword">
                        <FormLabel>Password</FormLabel>
                        <FormControl type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} value={password} />
                    </FormGroup>
                    <Button variant="primary" type="submit">
                        Log In
                    </Button>
                </Form>
            </dic>
        </>
    )
}

export default LoginPage
